import { useLocation } from "react-router-dom";
import React, { useState, useEffect, useRef } from "react";
import { Helmet } from "react-helmet";
import Stack from "@mui/material/Stack";
import Button, { ButtonProps } from "@mui/material/Button";
import { styled } from "@mui/material/styles";
import { useNavigate } from "react-router-dom";
import axios, { HeadersDefaults } from "axios";
import converterIcon from "../assets/converter-icon.png";
import swapIcon from "../assets/converter-swap-icon.png";
import { setConstantValue } from "typescript";

interface requestOptions {
  requestOptions: object;
}

// MUI Button Styles
const ColorButton = styled(Button)<ButtonProps>(({ theme }) => ({
  color: theme.palette.getContrastText("#002854"),
  marginBottom: "2rem",
  width: "10rem",
  height: "3.5rem",
  backgroundColor: "#002854",
  "&:hover": {
    backgroundColor: "#002854",
  },
  textTransform: "none",
}));

const Convert = () => {
  const [symbols, setSymbols] = useState({});
  const [symbolKeys, setSymbolKeys] = useState([]);
  const [selection, setSelection] = useState({
    currencies: ["USD", "SGD", "PHP", "EUR", "INR"],
    from: "USD",
    amount: "",
    to: "INR",
    result: "",
    date: "",
  });
  const { currencies, from, amount, to, result, date } = selection;

  useEffect(() => {
    if (amount === isNaN) {
      return;
    } else {
      const getCurrencyconvertTor = async () => {
        const response = await axios.get(
          `https://api.exchangeratesapi.io/latest?base=${from}`
        );
        console.log("response==>", response);
        const date = response.data.date;
        const result = (response.data.rates[to] * amount).toFixed(3);
        setSelection({
          ...selection,
          result,
          date,
        });
      };
      getCurrencyconvertTor();
    }
  }, [amount, from, to]);

  const location = useLocation();
  var myHeaders = new Headers();
  myHeaders.append("apikey", "dU1ta3aOck49UQP9EYU0vjvOOO5MPy1q");

  var requestOptions = {
    method: "GET",
    redirect: "follow",
    headers: myHeaders,
  };

  const getSymbolKeys = () => {
    let keys: any = [];
    for (let key in symbols) {
      keys.push(key);
    }
    setSymbolKeys(keys);
  };

  const fromRef = useRef();
  const toRef = useRef();

  const handleSelection = (e: any) => {
    setSelection({
      ...selection,
      [e.target.name]: e.target.value,
    });
  };

  const swapValues = () => {
    let from = selection.from;
    let to = selection.to;

    setSelection({
      ...selection,
      from: to,
      to: from,
    });
  };

  let username = location.state;
  return (
    <>
      <Helmet>
        <title>{`Log In`}</title>
      </Helmet>
      <section className="c-convert">
        <p>Welcome back {`${username}`}</p>
        <div className="c-convert__card">
          <div className="c-convert__card__logo">
            <img
              src={converterIcon}
              alt=""
              className="o-logo"
              draggable="false"
            />
            <div className="c-convert__card__logo__text">
              <p>Currency</p>
              <p>Transfer</p>
            </div>
          </div>
          <div className="c-convert__card__input-fields">
            <fieldset className="c-convert__card__input-fields__fieldset">
              <legend>From</legend>
              <select onChange={handleSelection} name="from" value={from}>
                {symbolKeys.map((item, index) => (
                  <option value={item} key={index}>
                    {item}
                  </option>
                ))}
              </select>
              <div className="c-convert__card__input-fields__fieldset__divider"></div>
              <div>
                <input type="text"></input>
              </div>
            </fieldset>
            <img
              src={swapIcon}
              alt=""
              draggable="false"
              onClick={() => {
                swapValues();
              }}
            />
            <fieldset className="c-convert__card__input-fields__fieldset">
              <legend>To</legend>
              <select onChange={handleSelection} name="to" value={to}>
                {symbolKeys.map((item, index) => (
                  <option value={item} key={index}>
                    {item}
                  </option>
                ))}
              </select>
              <div className="c-convert__card__input-fields__fieldset__divider"></div>
              <div>
                <input type="text"></input>
              </div>
            </fieldset>
          </div>
          <div className="c-convert__card__market-rate">
            <span>Market Rate N/A</span>
          </div>
          <Stack direction="column">
            <ColorButton
              variant="contained"
              onClick={() => {
                console.log(from, to);
              }}
            >
              Refresh
            </ColorButton>
          </Stack>
        </div>
      </section>
    </>
  );
};

export default Convert;
