import React, { useState } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Stack from "@mui/material/Stack";
import Button, { ButtonProps } from "@mui/material/Button";
import FormGroup from "@mui/material/FormGroup";
import { styled } from "@mui/material/styles";
import axios from "axios";
import { useNavigate } from "react-router-dom";

// MUI Button Styles
const ColorButton = styled(Button)<ButtonProps>(({ theme }) => ({
  color: theme.palette.getContrastText("#2F49D1"),
  backgroundColor: "#2F49D1",
  "&:hover": {
    backgroundColor: "#2F49D1",
  },
  textTransform: "none",
}));

interface Response {
  res: string;
}

export default function Signup() {
  // States
  const [usernameReg, setUsernameReg] = useState("");
  const [passwordReg, setPasswordReg] = useState("");
  const [confirmPass, setConfirmPass] = useState("");
  const [signUpErr, setSignUpErr] = useState(false);

  const navigate = useNavigate();

  console.log(usernameReg, passwordReg);

  const handleReroute = () => {
    navigate("/sign-in");
  };

  const handleSignup = async () => {
    if (passwordReg === confirmPass) {
      const body = {
        username: usernameReg,
        password: passwordReg,
      };
      try {
        await axios
          .post<Response[]>("http://localhost:8000/register", body)
          .then((res) => {
            console.log("SignUp successful", res);
            handleReroute();
          });
      } catch (error) {
        console.log(error);
      }
    } else {
      setSignUpErr(true);
    }
  };

  return (
    <section className="c-signup">
      <div className="c-signup__card">
        <FormGroup>
          <Box
            component="form"
            sx={{
              "& > :not(style)": { m: 1, width: "45ch" },
            }}
            noValidate
            autoComplete="off"
            className="c-signup__card__form"
          >
            {signUpErr && (
              <span className="signup-error-msg">Passwords don't match</span>
            )}
            {/* Username Input */}
            <label className="c-signup__card__form__input">
              <span>Username</span>

              <TextField
                id="outlined-basic"
                variant="outlined"
                placeholder="Enter your username"
                onChange={(e) => {
                  setUsernameReg(e.target.value);
                }}
              />
            </label>

            {/* Password Input */}
            <label className="c-signup__card__form__input">
              <span className={signUpErr ? "signup-error" : ""}>Password</span>

              <TextField
                variant="outlined"
                placeholder="Enter your password"
                onChange={(e) => {
                  setPasswordReg(e.target.value);
                }}
                type="password"
              />
            </label>

            {/* Password Confirmation Input */}
            <label className="c-signup__card__form__input">
              <span className={signUpErr ? "signup-error" : ""}>
                Confirm Password
              </span>

              <TextField
                variant="outlined"
                placeholder="Confirm your password"
                type="password"
              />
            </label>

            <Stack direction="column">
              <ColorButton variant="contained" onClick={handleSignup}>
                Sign Up
              </ColorButton>
            </Stack>
          </Box>
        </FormGroup>
      </div>
    </section>
  );
}
