import { useLocation } from "react-router-dom";
import React, { useState } from "react";
import { Helmet } from "react-helmet";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Stack from "@mui/material/Stack";
import Button, { ButtonProps } from "@mui/material/Button";
import FormGroup from "@mui/material/FormGroup";
import { styled } from "@mui/material/styles";
import { useNavigate } from "react-router-dom";
import axios, { HeadersDefaults } from "axios";
import converterIcon from "../assets/converter-icon.png";
import swapIcon from "../assets/converter-swap-icon.png";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormHelperText from "@mui/material/FormHelperText";
import FormControl from "@mui/material/FormControl";
import Select, { SelectChangeEvent } from "@mui/material/Select";

// MUI Button Styles
const ColorButton = styled(Button)<ButtonProps>(({ theme }) => ({
  color: theme.palette.getContrastText("#002854"),
  margin: "2rem",
  width: "9rem",
  height: "4rem",
  backgroundColor: "#002854",
  "&:hover": {
    backgroundColor: "#002854",
  },
  textTransform: "none",
}));

const Convert = () => {
  const location = useLocation();
  let username = location.state;
  console.log(location.state);
  return (
    <>
      <Helmet>
        <title>{`Log In`}</title>
      </Helmet>
      <section className="c-convert">
        <div className="c-convert__card">
          <div className="c-convert__card__logo">
            <img
              src={converterIcon}
              alt=""
              className="o-logo"
              draggable="false"
            />
            <div className="c-convert__card__logo__text">
              <p>Currency</p>
              <p>Transfer</p>
            </div>
          </div>
          <div className="c-convert__card__input-fields">
            <fieldset className="c-convert__card__input-fields__fieldset">
              <legend>From</legend>
              <select>
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
              </select>
              <div className="c-convert__card__input-fields__fieldset__divider"></div>
              <div>
                <input type="text"></input>
              </div>
            </fieldset>
            <img src={swapIcon} alt="" draggable="false" />
            <fieldset className="c-convert__card__input-fields__fieldset">
              <legend>To</legend>
              <select>
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
              </select>
              <div className="c-convert__card__input-fields__fieldset__divider"></div>
              <div>
                <input type="text"></input>
              </div>
            </fieldset>
          </div>
          <div className="c-convert__card__market-rate">
            <span>Market Rate N/A</span>
          </div>
          <Stack direction="column">
            <ColorButton variant="contained">Refresh</ColorButton>
          </Stack>
        </div>
      </section>
    </>
  );
};

export default Convert;
