import React, { useState } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Stack from "@mui/material/Stack";
import Button, { ButtonProps } from "@mui/material/Button";
import FormGroup from "@mui/material/FormGroup";
import { styled, alpha } from "@mui/material/styles";
import InputBase from "@mui/material/InputBase";

// MUI Button Styles
const ColorButton = styled(Button)<ButtonProps>(({ theme }) => ({
  color: theme.palette.getContrastText("#2F49D1"),
  backgroundColor: "#2F49D1",
  "&:hover": {
    backgroundColor: "#2F49D1",
  },
  textTransform: "none",
}));

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  return (
    <section className="c-login">
      <div className="c-login__card">
        <FormGroup>
          <Box
            component="form"
            sx={{
              "& > :not(style)": { m: 1, width: "45ch" },
            }}
            noValidate
            autoComplete="off"
            className="c-login__card__form"
          >
            {/* Username input */}
            <label className="c-login__card__form__input">
              <span>Username</span>

              <TextField
                id="outlined-basic"
                variant="outlined"
                placeholder="Enter your username"
              />
            </label>

            {/* Password input */}
            <label className="c-login__card__form__input">
              <span>Password</span>

              <TextField variant="outlined" placeholder="Enter your password" />
            </label>

            <div className="c-login__card__form__forgot">
              <span>Forgot your password?</span>
            </div>
            <label className="c-login__card__form__checkbox">
              <input type="checkbox" />
              <span className="c-login__card__form__checkbox__label">
                Remember me
              </span>
            </label>
            <Stack direction="column">
              <ColorButton variant="contained">Sign In</ColorButton>
            </Stack>
          </Box>
        </FormGroup>
      </div>
    </section>
  );
}
